class RefOnePath 
{
	OnePath op; 
	boolean bForward; 
	boolean bTransected = false; 

	RefOnePath(OnePath lop, boolean lbForward) 
	{
		op = lop; 
		bForward = lbForward; 
	}

	float GetTangent() 
	{
		return op.GetTangent(bForward); 
	}
}
